01371   if(SMTPdiostreamcfg)
01372     {
01373       delete SMTPdiostreamcfg;
01374 
01375       SMTPdiostreamcfg = NULL;
01376     }
01377 
01378   SMTPsenderisactive = false;
01379 
01380   //------------------------------------------
01381 
01382   SMSrecipients.DeleteContents();
01383   SMSrecipients.DeleteAll();
01384 
01385   SMSsenderisactive = false;
01386 
01387   //------------------------------------------
01388 
01389   WEBrecipients.DeleteContents();
01390   WEBrecipients.DeleteAll();
01391 
01392   if(WEBdiowebclient)
01393     {
01394       delete WEBdiowebclient;
01395       WEBdiowebclient = NULL;
01396     }
01397 
01398   WEBsenderisactive = false;
01399 
01400   //------------------------------------------
01401 
01402   UDPrecipients.DeleteContents();
01403   UDPrecipients.DeleteAll();
01404 
01405   if(UDPdiostream)
01406     {
01407       UDPdiostream->Close();
01408       delete UDPdiostream;
01409       UDPdiostream = NULL;
01410     }
01411 
01412   if(UDPdiostreamcfg)
01413     {
01414       delete UDPdiostreamcfg;
01415       UDPdiostreamcfg = NULL;
01416     }
01417 
01418   UDPsenderisactive = false;
01419 
01420   //------------------------------------------
01421 
01422   DeleteAllConditions();
01423 
01424   return true;
01425 }
