01468 bool DIOALERTS::Sender_SMTPSend(DIOALERT* alert)
01469 {
01470   if(!SMTP)   return false;
01471   if(!alert)  return false;
01472 
01473   SMTP->GetMessage()->DeleteAllLines();
01474 
01475   XSTRING appIDstring;
01476   XSTRING appverstring;
01477 
01478   SMTP->GetSenderName()->Set(alert->GetApplicationID()->Get());
01479 
01480   if(applicationversion || applicationsubversion  || applicationsubversionerr) appverstring.Format(__L(" %d.%d.%d"), applicationversion, applicationsubversion, applicationsubversionerr);
01481 
01482   appIDstring = alert->GetApplicationID()->Get();
01483   if(!appverstring.IsEmpty()) appIDstring += appverstring.Get();
01484 
01485   appIDstring += __L(": ");
01486   appIDstring += alert->GetTitle()->Get();
01487 
01488   SMTP->GetSubject()->Set(appIDstring);
01489 
01490   XSTRING body;
01491 
01492   body += alert->GetOrigin()->Get();
01493   body += __L("\r\n\r\n");
01494   body += alert->Get_Message()->Get();
01495 
01496   SMTP->GetMessage()->AddLine(body);
01497 
01498   SMTP->SetXPriority(DIOSMTPXPRIORITY_HIGH);
01499 
01500   return SMTP->Send();
01501 }
