01515 bool DIOALERTS::Sender_SMSSend(DIOALERT* alert)
01516 {
01517   if(!alert) return false;
01518 
01519   bool status = false;
01520 
01521   DIOATCMDGSM* SMSdioatcmdgsm = new DIOATCMDGSM( SMSdiostream);
01522   if(SMSdioatcmdgsm)
01523     {
01524       if(SMSdioatcmdgsm->Ini(30, false, false))
01525         {
01526           XSTRING smstext;
01527 
01528           if(alert->GetApplicationID()->GetSize())
01529             {
01530               XSTRING appverstring;
01531 
01532               if(applicationversion || applicationsubversion  || applicationsubversionerr) appverstring.Format(__L(" %d.%d.%d"), applicationversion, applicationsubversion, applicationsubversionerr);
01533 
01534               smstext += __L("<");
01535               smstext += alert->GetApplicationID()->Get();
01536 
01537               if(!appverstring.IsEmpty()) smstext += appverstring.Get();
01538 
01539               smstext += __L("> ");
01540             }
01541 
01542 
01543           if(alert->GetTitle()->GetSize())
01544             {
01545               smstext += alert->GetTitle()->Get();
01546               smstext += __L(" ");
01547             }
01548 
01549 
01550           if(alert->GetOrigin()->GetSize())
01551             {
01552               smstext += __L("(");
01553               smstext += alert->GetOrigin()->Get();
01554               smstext += __L(")");
01555             }
01556 
01557           if(alert->Get_Message()->GetSize())
01558             {
01559               smstext += __L(":");
01560               smstext += alert->Get_Message()->Get();
01561             }
01562 
01563           if(smstext.GetSize()>=160) smstext.AdjustSize(160);
01564 
01565           status = true;
01566 
01567           for(int c=0; c<(int)SMSrecipients.GetSize(); c++)
01568             {
01569               if(SMSdioatcmdgsm->SendSMS(SMSrecipients.Get(c)->Get(), smstext.Get()) != DIOATCMD_ERROR_NONE)  status = false;
01570             }
01571 
01572           SMSdioatcmdgsm->End();
01573         }
01574 
01575       delete SMSdioatcmdgsm;
01576     }
01577 
01578   return status;
01579 }
