01593 bool DIOALERTS::Sender_WEBSend(DIOALERT* alert)
01594 {
01595   if(WEBrecipients.IsEmpty()) return false;
01596 
01597   DIOURL    url;
01598   XSTRING   applicationIDstring;
01599   XSTRING   applicationverstring; 
01600   XBUFFER   webpage;
01601   bool      status  = true;
01602 
01603   if(applicationversion || applicationsubversion  || applicationsubversionerr) applicationverstring.Format(__L(" %d.%d.%d"), applicationversion, applicationsubversion, applicationsubversionerr);
01604 
01605   applicationIDstring = alert->GetApplicationID()->Get();
01606   if(!applicationverstring.IsEmpty()) applicationIDstring += applicationverstring.Get();
01607 
01608   for(int c=0; c<(int)WEBrecipients.GetSize(); c++)
01609     {
01610       DIOURL    part;
01611       
01612       url = WEBrecipients.Get(c)->Get();
01613 
01614       if(WEBisuseget)
01615         {
01616           url.Slash_Delete();
01617           url.AddFormat(__L("?%s") , WEBcommand.Get());
01618 
01619           if(!applicationIDstring.IsEmpty()) 
01620             {
01621               part = applicationIDstring.Get();            
01622               part.EncodeUnsafeChars();                  
01623               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_APPLICATIONID, part.Get());       
01624             }      
01625 
01626           if(alert->GetLevel())
01627             {
01628               part.Format(__L("%d"), alert->GetLevel());   
01629               part.EncodeUnsafeChars();                  
01630               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_LEVEL, part.Get());               
01631             }
01632 
01633           if(!alert->GetTitle()->IsEmpty())
01634             {
01635               part = alert->GetTitle()->Get();             
01636               part.EncodeUnsafeChars();    
01637               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_TITLE, part.Get());              
01638             }
01639               
01640           if(!alert->GetOrigin()->IsEmpty())             
01641             {
01642               part = alert->GetOrigin()->Get();            
01643               part.EncodeUnsafeChars();                  
01644               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_ORIGIN, part.Get());              
01645             }
01646 
01647           if(!alert->Get_Message()->IsEmpty())
01648             {
01649               part = alert->Get_Message()->Get();          
01650               part.EncodeUnsafeChars();                  
01651               url.AddFormat(__L("&%s=%s"), DIOALERTS_QSPARAM_MESSAGE, part.Get());             
01652             }
01653              
01654           status = WEBdiowebclient->Get(url.Get(), webpage);          
01655         }
01656        else
01657         {
01658           XSTRING   contents;
01659           XFILEJSON json;
01660           XBUFFER   postbuffer;
01661 
01662           XFILEJSONOBJECT* root   = new XFILEJSONOBJECT();
01663           if(root) 
01664             {
01665               XFILEJSONOBJECT* main  = new XFILEJSONOBJECT();
01666               if(main) 
01667                 {
01668                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_APPLICATIONID , applicationIDstring.Get());                  
01669                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_LEVEL         , (int)alert->GetLevel());          
01670                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_TITLE         , alert->GetTitle()->Get());
01671                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_ORIGIN        , alert->GetOrigin()->Get());
01672                   XFILEJSON_ADDVALUE(main, DIOALERTS_QSPARAM_MESSAGE       , alert->Get_Message()->Get());
01673 
01674                   XFILEJSON_ADDVALUE(root, WEBcommand.Get(), main);                      
01675 
01676                   json.SetRoot(root);
01677                   json.EncodeAllLines(false);
01678 
01679                   for(int c=0; c<json.GetNLines(); c++)
01680                     {
01681                       XSTRING* string = json.GetLine(c);
01682                       if(string)
01683                         {                          
01684                           contents.Add(string->Get());                 
01685                         }
01686                      }
01687 
01688                   json.DeleteAllLines();
01689                   json.DeleteAllObjects();
01690                 }
01691             }                    
01692 
01693           //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("%s"), contents.Get());
01694 
01695           XBUFFER postdata;
01696           
01697           contents.ConvertToASCII(postdata);          
01698           postbuffer.Add(postdata.Get(), contents.GetSize());
01699           
01700           status = WEBdiowebclient->Post(url.Get(), webpage, &postbuffer);      
01701         }        
01702     }
01703 
01704   return status;
01705 }
