01719 bool DIOALERTS::Sender_UDPSend(DIOALERT* alert)
01720 {
01721   if(!alert)            return false;
01722   if(!UDPdiostreamcfg)  return false;
01723   if(!UDPdiostream)     return false;
01724 
01725   XBUFFER     xbuffer;
01726   HASHCRC32   hashcrc32;
01727   XDWORD      CRC32;
01728 
01729   xbuffer.Delete();
01730 
01731   // Date time
01732   xbuffer.Add((XDWORD)alert->GetDateTime()->GetDay());                                                              // Day
01733   xbuffer.Add((XDWORD)alert->GetDateTime()->GetMonth());                                                            // Month
01734   xbuffer.Add((XDWORD)alert->GetDateTime()->GetYear());                                                             // Year
01735   xbuffer.Add((XDWORD)alert->GetDateTime()->GetHours());                                                            // Hours
01736   xbuffer.Add((XDWORD)alert->GetDateTime()->GetMinutes());                                                          // Minutes
01737   xbuffer.Add((XDWORD)alert->GetDateTime()->GetSeconds());                                                          // Seconds
01738 
01739   xbuffer.Add((XDWORD)alert->GetApplicationID()->GetSize());    xbuffer.Add((*alert->GetApplicationID()));          // Aplication String
01740   xbuffer.Add((XDWORD)applicationversion);                                                                          // Aplicatcion version
01741   xbuffer.Add((XDWORD)applicationsubversion);                                                                       // Aplication  subversion
01742   xbuffer.Add((XDWORD)applicationsubversionerr);                                                                    // Application subversion err
01743 
01744   xbuffer.Add((XDWORD)alert->GetID());                                                                              // ID  
01745   xbuffer.Add((XDWORD)alert->GetLevel());                                                                           // Level
01746   xbuffer.Add((XDWORD)alert->GetOrigin()->GetSize());           xbuffer.Add((*alert->GetOrigin()));                 // Origin
01747   xbuffer.Add((XDWORD)alert->GetTitle()->GetSize());            xbuffer.Add((*alert->GetTitle()));                  // Title
01748   xbuffer.Add((XDWORD)alert->Get_Message()->GetSize());         xbuffer.Add((*alert->Get_Message()));               // Message
01749 
01750   hashcrc32.ResetResult();
01751   hashcrc32.Do(xbuffer);
01752 
01753   CRC32 = hashcrc32.GetResultCRC32();
01754 
01755   xbuffer.Add((XDWORD)CRC32);
01756 
01757   bool status;
01758   int  nsent = 0;
01759 
01760   for(int c=0; c<(int)UDPrecipients.GetSize(); c++)
01761     {
01762       DIOIP* ip = UDPrecipients.Get(c);
01763       if(ip)
01764         {
01765           XSTRING IPstring;
01766 
01767           ip->GetXString(IPstring);
01768 
01769           UDPdiostreamcfg->SetMode(DIOSTREAMMODE_CLIENT);
01770           UDPdiostreamcfg->GetRemoteURL()->Set(IPstring.Get());
01771           UDPdiostreamcfg->SetIsUsedDatagrams(true);
01772 
01773           status = UDPdiostream->Open();
01774           if(status)
01775             {
01776               status = UDPdiostream->Write(xbuffer.Get(), xbuffer.GetSize())?true:false;
01777               if(status) nsent++;
01778               UDPdiostream->Close();
01779             }
01780         }
01781     }
01782 
01783   return (UDPrecipients.GetSize() == nsent)?true:false;
01784 }
