00269 int DIOAPPLICATIONUPDATE_VERSIONDATA::Compare(XDWORD version, XDWORD subversion, XDWORD subversionerror)
00270 {
00271   // Equal;
00272   if((GetVersion() == version) && (GetSubVersion() == subversion) && (GetSubVersionError() == subversionerror)) return 0;
00273 
00274   bool ismayorversion = false;
00275 
00276   if(GetVersion() > version)
00277     {
00278       ismayorversion = true;
00279     }
00280    else
00281     {
00282       if(GetVersion() == version)
00283         {
00284           if(GetSubVersion() > subversion)
00285             {
00286               ismayorversion = true;
00287             }
00288            else
00289             {
00290               if(GetSubVersion() == subversion)
00291                 {
00292                   if(GetSubVersionError() > subversionerror)  ismayorversion = true;
00293                 }
00294             }
00295         }
00296     }
00297 
00298   return ismayorversion?1:-1;
00299 }
