01059                    status = xfile->Rename(xpathrootappadd, xpathrootappaddrename);
01060                    GEN_XFACTORY.Delete_File(xfile);
01061                  }
01062             }
01063            else
01064             {
01065               error = xevent.GetError();
01066               break;
01067             }
01068         }
01069 
01070       if(status)
01071         {
01072           status = CopyFile(xpathupdateadd, xpathrootappadd);
01073           if(status)
01074             {
01075                if(filestoupdate.Get(c)->HasSpecialControlUpdate() && !xpathrootappaddrename.IsEmpty())
01076                  {
01077                    // Try to erase. In windows probably fails.
01078 
01079                    XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01080                    if(xfile)
01081                      {
01082                        xfile->Erase(xpathrootappaddrename);
01083                        GEN_XFACTORY.Delete_File(xfile);
01084                      }
01085                  }
01086             }
01087         }
01088 
01089       DIOAPPLICATIONUPDATE_XEVENT xevent(this, (restore?DIOAPPLICATIONUPDATE_XEVENT_TYPE_RESTOREUPDATEFILE:DIOAPPLICATIONUPDATE_XEVENT_TYPE_UPDATEFILE));
01090 
01091       xevent.SetFilesToUpdate(&filestoupdate);
01092       xevent.SetIndexOfOperation(c);
01093       xevent.SetError(status?DIOAPPLICATIONUPDATE_NOT_ERROR:DIOAPPLICATIONUPDATE_ERROR_INVALIDOPERATION);
01094 
01095       PostEvent(&xevent);
01096 
01097       if(!status)
01098         {
01099           error  = DIOAPPLICATIONUPDATE_ERROR_COPYORIGINALFILE;
01100           break;
01101         }
01102     }
01103 
01104   return status;
01105 }
