01258 bool DIOAPPLICATIONUPDATE::GetListOfVersionsToRestore(XVECTOR<DIOAPPLICATIONUPDATE_VERSIONDATA*>& listversionstorestore)
01259 {
01260   XDIRELEMENT xdirelement;
01261   XDIR*       xdir;
01262   XSTRING     searchstring;
01263   XSTRING     dir_mask;
01264   bool        status = true;
01265 
01266   GEN_XFACTORY_CREATE(xdir, Create_Dir())
01267   if(!xdir) 
01268     {
01269       return false;
01270     }
01271 
01272   dir_mask  = DIOAPPLICATIONUPDATE_RESTOREDIR_ID;
01273   dir_mask += DIOAPPLICATIONUPDATE_RESTOREDIR_MASK;
01274 
01275   searchstring  = DIOAPPLICATIONUPDATE_RESTOREDIR_ID;
01276   searchstring += __L("*");
01277 
01278   if(xdir->FirstSearch(xpathrootapp, searchstring.Get() , &xdirelement))
01279     {
01280       do{  xdirelement.GetNameFile()->Get();
01281 
01282           if(xdirelement.GetType() == XDIRELEMENTTYPE_DIR)
01283             {
01284               if(!xdirelement.GetNameFile()->Find(DIOAPPLICATIONUPDATE_RESTOREDIR_ID, false))
01285                 {
01286                   int version         = 0;
01287                   int subversion      = 0;
01288                   int subversionerror = 0;
01289 
01290                   xdirelement.GetNameFile()->UnFormat(dir_mask.Get(), &version, &subversion, &subversionerror);
01291 
01292                   if(version || subversion || subversionerror)
01293                     {
01294                       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = new DIOAPPLICATIONUPDATE_VERSIONDATA();
01295                       if(versiondata)
01296                         {
01297                           versiondata->SetVersion(version);
01298                           versiondata->SetSubVersion(subversion);
01299                           versiondata->SetSubVersionError(subversionerror);
01300 
01301                           listversionstorestore.Add(versiondata);
01302                         }
01303                     }
01304                 }
01305             }
01306 
01307         } while(xdir->NextSearch(&xdirelement));
01308     }
01309 
01310   GEN_XFACTORY.Delete_Dir(xdir);
01311 
01312   return status;
01313 }
