01328 bool DIOAPPLICATIONUPDATE::GetPreviousVersionToRestore(XVECTOR<DIOAPPLICATIONUPDATE_VERSIONDATA*>& listversionstorestore, DIOAPPLICATIONUPDATE_VERSIONDATA& versiontorestore)
01329 {
01330   XVECTOR<int> indexversiontorestore;
01331 
01332   for(XDWORD c=0; c<listversionstorestore.GetSize(); c++)
01333     {
01334       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(c);
01335       if(versiondata)
01336         {
01337           int comparateversion = versiondata->Compare(applicationversion, applicationsubversion, applicationsubversionerror);
01338           if(comparateversion < 0)
01339             {
01340               indexversiontorestore.Add(c);
01341             }
01342         }
01343     }
01344 
01345   if(!indexversiontorestore.GetSize()) 
01346     {
01347       return false;
01348     }
01349 
01350   if(indexversiontorestore.GetSize() == 1)
01351     {
01352       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(0);
01353       if(!versiondata) return false;
01354 
01355       versiontorestore.CopyFrom(versiondata);
01356     }
01357    else
01358     {
01359       int indexprevius = indexversiontorestore.GetLast();
01360 
01361       for(XDWORD c=0; c<indexversiontorestore.GetSize(); c++)
01362         {
01363           DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(indexprevius);
01364           DIOAPPLICATIONUPDATE_VERSIONDATA* versiondatacheck = listversionstorestore.Get(indexversiontorestore.Get(c));
01365           if(versiondatacheck && versiondata)
01366             {
01367               if(versiondatacheck->Compare(versiondata->GetVersion(), versiondata->GetSubVersion(), versiondata->GetSubVersionError()) == 1)
01368                 {
01369                   indexprevius = indexversiontorestore.Get(c);
01370                 }
01371             }
01372         }
01373 
01374       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(indexprevius);
01375       if(!versiondata) return false;
01376 
01377       versiontorestore.CopyFrom(versiondata);
01378     }
01379 
01380   return true;
01381 }
