01494 bool DIOAPPLICATIONUPDATE::EliminateOldRestorations(int maxrestorations)
01495 {
01496   XVECTOR<DIOAPPLICATIONUPDATE_VERSIONDATA*>  listversionstorestore;
01497   XVECTOR<int>                                indexversiontorestore;
01498   bool                                        status = false;
01499 
01500   GetListOfVersionsToRestore(listversionstorestore);
01501 
01502   for(XDWORD c=0; c<listversionstorestore.GetSize(); c++)
01503     {
01504       DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(c);
01505       if(versiondata)
01506         {
01507           int comparateversion = versiondata->Compare(applicationversion, applicationsubversion, applicationsubversionerror);
01508           if(comparateversion < 0)
01509             {
01510               indexversiontorestore.Add(c);
01511             }
01512         }
01513     }
01514 
01515   if(indexversiontorestore.GetSize())
01516     {
01517       for(XDWORD c=0; c<indexversiontorestore.GetSize()-1; c++)
01518         {
01519           for(XDWORD d=1; d<indexversiontorestore.GetSize(); d++)
01520             {
01521               DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(d);
01522               DIOAPPLICATIONUPDATE_VERSIONDATA* versiondatacheck = listversionstorestore.Get(indexversiontorestore.Get(d-1));
01523               if(versiondatacheck && versiondata)
01524                 {
01525                   if(versiondatacheck->Compare(versiondata->GetVersion(), versiondata->GetSubVersion(), versiondata->GetSubVersionError()) == 1)
01526                     {
01527                       int index = indexversiontorestore.Get(c);
01528 
01529                       indexversiontorestore.Set(c, indexversiontorestore.Get(d));
01530                       indexversiontorestore.Set(d, index);
01531                     }
01532                 }
01533             }
01534         }
01535     }
01536 
01537   status = true;
01538 
01539   int toeliminate =  indexversiontorestore.GetSize()-maxrestorations;
01540   if(toeliminate>0)
01541     {
01542       for(int c=0; c<toeliminate; c++)
01543         {
01544           DIOAPPLICATIONUPDATE_VERSIONDATA* versiondata = listversionstorestore.Get(indexversiontorestore.Get(c));
01545           if(versiondata)
01546             {
01547               XPATH     xpath;
01548               XSTRING   dir_mask;
01549 
01550               xpath = xpathrootapp;
01551               xpath.Slash_Add();
01552 
01553               dir_mask  = DIOAPPLICATIONUPDATE_RESTOREDIR_ID;
01554               dir_mask += DIOAPPLICATIONUPDATE_RESTOREDIR_MASK;
01555 
01556               xpath.AddFormat(dir_mask.Get(), versiondata->GetVersion(), versiondata->GetSubVersion(), versiondata->GetSubVersionError());
01557 
01558               XDIR* GEN_XFACTORY_CREATE(xdir, Create_Dir());
01559               if(xdir)
01560                 {
01561                   status = xdir->Delete(xpath.Get(), true);
01562                   GEN_XFACTORY.Delete_Dir(xdir);
01563 
01564                   if(!status) break;
01565                 }
01566 
01567               //XTRACE_PRINTCOLOR(3, xpath.Get());
01568             }
01569         }
01570     }
01571 
01572   listversionstorestore.DeleteContents();
01573   listversionstorestore.DeleteAll();
01574 
01575   indexversiontorestore.DeleteAll();
01576 
01577   return status;
01578 }
