01592 bool DIOAPPLICATIONUPDATE::IsNewVersion(DIOAPPLICATIONUPDATE_VERSIONDATA& updateversiondata)
01593 {
01594   int versioncomparation = updateversiondata.Compare(applicationversion, applicationsubversion, applicationsubversionerror);
01595 
01596   //----------------------------------------------------------------------------------------
01597 
01598   DIOAPPLICATIONUPDATE_XEVENT xevent(this, DIOAPPLICATIONUPDATE_XEVENT_TYPE_CHECKVERSIONAVAILABLE);
01599 
01600   xevent.SetVersionComparation(versioncomparation);
01601   xevent.SetVersionData(&updateversiondata);
01602   xevent.SetFilesToUpdate(&filestoupdate);
01603   xevent.SetError(DIOAPPLICATIONUPDATE_NOT_ERROR);
01604 
01605   PostEvent(&xevent);
01606 
01607   //----------------------------------------------------------------------------------------
01608 
01609   return (versioncomparation == 1)?true:false;
