01670 bool DIOAPPLICATIONUPDATE::FilesToUpdate_CreateList(XPATH& xpathupdate)
01671 {
01672   XDIRELEMENT xdirelement;
01673   XDIR*       xdir;
01674   bool        status = true;
01675 
01676   GEN_XFACTORY_CREATE(xdir, Create_Dir())
01677   if(!xdir) return false;
01678 
01679   if(xdir->FirstSearch(xpathupdate, __L("*") , &xdirelement))
01680     {
01681       XPATH xpathadd;
01682 
01683       do{ xpathadd   = xpathupdate;
01684           xpathadd.Slash_Add();
01685           xpathadd  += xdirelement.GetNameFile()->Get();
01686 
01687           // XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("%s"), xpathadd.Get());  
01688 
01689           if(xdirelement.GetType() == XDIRELEMENTTYPE_FILE)
01690             {
01691               HASHCRC32 CRC32file;
01692               XQWORD    sizefile = 0;
01693 
01694               CRC32file.Do(xpathadd);
01695 
01696               XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
01697               if(xfile)
01698                 {
01699                   if(xfile->Open(xpathadd))
01700                     {
01701                       sizefile = xfile->GetSize();
01702                       xfile->Close();
01703                     }
01704 
01705                   GEN_XFACTORY.Delete_File(xfile);
01706                 }
01707 
01708               DIOAPPLICATIONUPDATE_FILE* updatefile = new DIOAPPLICATIONUPDATE_FILE(xpathadd, CRC32file.GetResultCRC32(), sizefile);
01709               if(updatefile)
01710                 {
01711                   XSTRING filename;
01712                   XSTRING fileext;
01713 
01714                   updatefile->GetXPathFile()->GetNamefile(filename);
01715                   updatefile->GetXPathFile()->GetExt(fileext);
01716 
01717                   if(!filename.Compare(applicationname, true))
01718                     {
01719                       updatefile->SetHasSpecialControlUpdate(true);
01720                     }
01721 
01722                   filestoupdate.Add(updatefile);
01723                 }
01724             }
01725 
01726           if(xdirelement.GetType() == XDIRELEMENTTYPE_DIR)
01727             {
01728               if(!FilesToUpdate_CreateList(xpathadd))
01729                 {
01730                   status = false;
01731                   break;
01732                 }
01733             }
01734 
01735         } while(xdir->NextSearch(&xdirelement));
01736     }
01737 
01738   GEN_XFACTORY.Delete_Dir(xdir);
01739 
01740   return status;
01741 }
