01755 bool DIOAPPLICATIONUPDATE::FilesToUpdate_EliminateFixedDir(XPATH& xpath)
01756 {
01757   XDWORD c = 0;
01758 
01759   if(!filestoupdate.GetSize()) return false;
01760 
01761   while(c < filestoupdate.GetSize())
01762     {
01763       DIOAPPLICATIONUPDATE_FILE* updatefile = filestoupdate.Get(c);
01764       if(updatefile)
01765         {
01766            updatefile->GetXPathFile()->DeleteCharacters(0, xpath.GetSize()+1);
01767            c++;
01768         }
01769     }
01770 
01771   return true;
01772 }
01773 
01774 
01786 bool DIOAPPLICATIONUPDATE::FilesToUpdate_CheckListChangedFiles(XPATH& xpathupdate)
01787 {
01788   XDWORD c = 0;
01789 
01790   while(c < filestoupdate.GetSize())
01791     {
01792       DIOAPPLICATIONUPDATE_FILE* updatefile = filestoupdate.Get(c);
01793       if(updatefile)
01794         {
01795           HASHCRC32 CRC32file;
01796           XPATH     xpath;
01797 
01798           xpath  = xpathrootapp;
01799           xpath.Slash_Add();
01800           xpath += updatefile->GetXPathFile()->Get();
01801 
01802           CRC32file.Do(xpath);
01803 
01804           if(CRC32file.GetResultCRC32() == updatefile->GetCRC32())
01805             {
01806               filestoupdate.Delete(updatefile);
01807               delete updatefile;
01808             }
01809            else
01810             {
01811               c++;
01812             }
01813         }
01814     }
01815 
01816   return true;
01817 }
