01852 bool DIOAPPLICATIONUPDATE::CopyFile(XPATH& origin, XPATH& target)
01853 {
01854   XFILE* xfile[2] = { NULL, NULL };
01855   bool   status   = false;
01856 
01857   GEN_XFACTORY_CREATE(xfile[0], Create_File())
01858   GEN_XFACTORY_CREATE(xfile[1], Create_File())
01859 
01860   if(xfile[0] && xfile[1])
01861     {
01862       if(xfile[0]->Open(origin))
01863         {
01864           if(xfile[1]->Create(target))
01865             {
01866               #define COPYFILEBLOCKSIZE  500*1024
01867 
01868               XBUFFER xbuffer;
01869               XDWORD  br = COPYFILEBLOCKSIZE;
01870 
01871               xbuffer.Resize(COPYFILEBLOCKSIZE);
01872 
01873               do{ xfile[0]->Read(xbuffer.Get(), &br);
01874                   if(!br) break;
01875 
01876                   if(!xfile[1]->Write(xbuffer.Get(), br)) break;
01877 
01878                 } while(br == COPYFILEBLOCKSIZE);
01879 
01880 
01881               xfile[1]->Close();
01882 
01883               status = true;
01884             }
01885 
01886           xfile[0]->Close();
01887         }
01888     }
01889 
01890   GEN_XFACTORY.Delete_File(xfile[0]);
01891   GEN_XFACTORY.Delete_File(xfile[1]);
01892 
01893   if(status)
01894     {
01895       status = false;
01896 
01897       HASHCRC32* CRC32file[2];
01898 
01899       CRC32file[0] = new HASHCRC32();
01900       CRC32file[1] = new HASHCRC32();
01901 
01902       if(CRC32file[0] && CRC32file[1])
01903         {
01904           CRC32file[0]->Do(origin);
01905           CRC32file[1]->Do(target);
01906 
01907           if(CRC32file[0]->GetResultCRC32() == CRC32file[1]->GetResultCRC32()) status = true;
01908         }
01909 
01910       delete CRC32file[0];
01911       delete CRC32file[1];
01912     }
01913 
01914   return status;
01915 }
