00923 bool DIOCHECKTCPIPCONNECTIONS::Connections_WaitToAllChecked(int timeout)
00924 {
00925   if(!connections.GetSize()) return false;
00926 
00927   XTIMER* GEN_XFACTORY_CREATE(xtimer, CreateTimer())
00928   if(!xtimer) return false;
00929 
00930   bool status = true;
00931 
00932   xtimer->Reset();
00933 
00934   SetIsCheckTimeConnections(false);
00935 
00936   while(1)
00937     {
00938       if(timeout != XTIMER_INFINITE)
00939         {
00940           if(xtimer->GetMeasureSeconds() >= (XDWORD)timeout)
00941             {
00942               status = false;
00943               break;
00944             }
00945         }
00946 
00947       if(Connections_IsAllChecked()) break;
00948 
00949       GEN_XSLEEP.MilliSeconds(100);
00950     }
00951 
00952   GEN_XFACTORY.DeleteTimer(xtimer);
00953 
00954   SetIsCheckTimeConnections(true);
00955 
00956   return status;
00957 }
