01107 void DIOCHECKTCPIPCONNECTIONS::ThreadCheckConnections(void* param)
01108 {
01109   DIOCHECKTCPIPCONNECTIONS* checkconnections = (DIOCHECKTCPIPCONNECTIONS*)param;
01110   if(!checkconnections) return;
01111 
01112   if(!checkconnections->xmutexconnections)                return;
01113   if(!checkconnections->xtimerfortimeconnectionchecks)    return;
01114 
01115   static int index = 0;
01116 
01117   int nconnections = checkconnections->connections.GetSize();
01118   if(!nconnections) return;
01119 
01120   if(checkconnections->dispersionmode)
01121     {
01122       DIOCHECKTCPIPCONNECTION* checkconnection = checkconnections->connections.Get(index);
01123       if(!checkconnection)
01124         {
01125           index = 0;
01126           checkconnection = checkconnections->connections.Get(index);
01127         }
01128 
01129       if(checkconnection)
01130         {
01131           checkconnections->xmutexconnections->Lock();
01132 
01133           GEN_DIOPING.Set(checkconnection->GetURL()->Get());
01134 
01135           bool connexionstatus = GEN_DIOPING.Do(DIOCHECKTCPIPCONNECTIONS_DEFAULTNCHECKSFOREVERYCONNECTION, DIOCHECKTCPIPCONNECTIONS_DEFAULTTIMERCONNECTIONCHECK, checkconnections->validsomeisconnected);
01136           if(connexionstatus != checkconnection->IsConnected()) checkconnection->ResetTimeConnexionStatus();
01137 
01138           checkconnection->SetIsConnected(connexionstatus);
01139           checkconnection->IncNChecks();
01140 
01141           if(checkconnection->IsConnected())
01142                  checkconnection->SetElapsedTime(GEN_DIOPING.CalculateMeanTime());
01143             else checkconnection->SetElapsedTime(0);
01144 
01145           index++;
01146 
01147           checkconnections->xmutexconnections->UnLock();
01148 
01149           if(checkconnections->IsCheckTimeConnections())
01150             {
01151               for(int e=0; e<checkconnections->timeconnectionchecks; e++)
01152                 {
01153                   if(!checkconnections->threadcheckconnections->IsRunning()) break;
01154 
01155                   for(int d=0; d<10; d++)
01156                     {
01157                       if(!checkconnections->threadcheckconnections->IsRunning()) break;
01158                       GEN_XSLEEP.MilliSeconds(100);
01159                     }
01160                 }
01161             }
01162 
01163 
01164         }
01165     }
01166    else
01167     {
01168       checkconnections->xmutexconnections->Lock();
01169 
01170       for(int c=0; c<nconnections; c++)
01171         {
01172           DIOCHECKTCPIPCONNECTION* checkconnection = checkconnections->connections.Get(c);
01173           if(checkconnection)
01174             {
01175               GEN_DIOPING.Set(checkconnection->GetURL()->Get());
01176 
01177               bool connexionstatus = GEN_DIOPING.Do(DIOCHECKTCPIPCONNECTIONS_DEFAULTNCHECKSFOREVERYCONNECTION, DIOCHECKTCPIPCONNECTIONS_DEFAULTTIMERCONNECTIONCHECK, checkconnections->validsomeisconnected);
01178               if(connexionstatus != checkconnection->IsConnected()) checkconnection->ResetTimeConnexionStatus();
01179 
01180               checkconnection->SetIsConnected(connexionstatus);
01181               checkconnection->IncNChecks();
01182 
01183               if(checkconnection->IsConnected())
01184                       checkconnection->SetElapsedTime(GEN_DIOPING.CalculateMeanTime());
01185                 else checkconnection->SetElapsedTime(0);
01186 
01187               if((checkconnections->validsomeisconnected) && (checkconnection->IsConnected())) break;
01188               if(!checkconnections->threadcheckconnections->IsRunning()) break;
01189             }
01190         }
01191 
01192       checkconnections->xmutexconnections->UnLock();
01193 
01194       if(checkconnections->IsCheckTimeConnections())
01195         {
01196           for(int e=0; e<checkconnections->timeconnectionchecks; e++)
01197             {
01198               if(!checkconnections->threadcheckconnections->IsRunning()) break;
01199 
01200               for(int d=0; d<10; d++)
01201                 {
01202                   if(!checkconnections->threadcheckconnections->IsRunning()) break;
01203                   GEN_XSLEEP.MilliSeconds(100);
01204                 }
01205             }
