00660 DIOGPIO_ENTRY* DIOGPIO::GPIOEntry_GetByID(XDWORD ID)
00661 {
00662   if(GPIOentrys.IsEmpty()) return NULL;
00663 
00664   for(XDWORD c=0; c<GPIOentrys.GetSize(); c++)
00665     {
