00108 void DIOI2CLSM303DLHCVECTOR::Cross(const DIOI2CLSM303DLHCVECTOR& a, const DIOI2CLSM303DLHCVECTOR& b, DIOI2CLSM303DLHCVECTOR& out)
00109 {
00110   out.x = a.y * b.z - a.z * b.y;
00111   out.y = a.z * b.x - a.x * b.z;
