00395 bool DIOI2C3DACCELEROMETERLSM303DLHC::Configure()
00396 {
00397   if(!IsInitialized()) return false;
00398 
00399   XBYTE value;
00400 
00401   value  = 0x00;
00402   value |= 0x01 << 5;     // Normal mode
00403   value |= 0x07;          // X/Y/Z axis enable.
00404   if(!WriteRegister(DIOI2CLSM303DLHC_CTRL_REG1_A , value))    return false;
00405   value  = 0x00;
00406   ReadRegister(DIOI2CLSM303DLHC_CTRL_REG1_A, value);
00407 
00408   value  = 0x00;
00409   value |= 0x01 << 6;     // 1: data MSB @ lower address
00410   value |= 0x01 << 4;     // +/- 4g
00411   if(!WriteRegister(DIOI2CLSM303DLHC_CTRL_REG4_A, value))   return false;
00412 
00413   return true;
00414 }
