00489 {
00490   if(!IsInitialized()) return false;
00491 
00492   XBYTE value;
00493 
00494   value  = 0x00;
00495   value |= 0x04 << 2;     // Minimum data output rate = 15Hz
00496   if(!WriteRegister(DIOI2CLSM303DLHC_CRA_REG_M, value))           return false;
00497 
00498   value  = 0x00;
00499 //value |= 0x01 << 5;     // +-1.3Gauss
00500   value |= 0x07 << 5;     // +-8.1Gauss
00501   if(!WriteRegister(DIOI2CLSM303DLHC_CRB_REG_M, value))           return false;
00502 
