00718 bool DIOI2C6AXISTRACKINGLSM303DLHC::Read(DIOI2CLSM303DLHCVECTOR& a, DIOI2CLSM303DLHCVECTOR& c)
00719 {
00720   if(!accelerometer)  return false;
00721   if(!compass)        return false;
00722 
00723   XWORD ax = 0;
00724   XWORD ay = 0;
00725   XWORD az = 0;
00726   XWORD cx = 0;
00727   XWORD cy = 0;
00728   XWORD cz = 0;
00729 
00730   if(!Read(ax, ay, az, cx, cy, cz)) return false;
00731 
00732   // Perform simple lowpass filtering Intended to stabilize heading despite device vibration such as on a UGV
00733   filter_ax += ax - (filter_ax >> DIOI2CLSM303DLHC_FILTERSHIFT);
00734   filter_ay += ay - (filter_ay >> DIOI2CLSM303DLHC_FILTERSHIFT);
00735   filter_az += az - (filter_az >> DIOI2CLSM303DLHC_FILTERSHIFT);
00736 
00737   a.x = (float)(filter_ax >> DIOI2CLSM303DLHC_FILTERSHIFT);
00738   a.y = (float)(filter_ay >> DIOI2CLSM303DLHC_FILTERSHIFT);
00739   a.z = (float)(filter_az >> DIOI2CLSM303DLHC_FILTERSHIFT);
00740 
00741   // offset and scale
00742   c.x = (cx + offset_x) * scale_x;
00743   c.y = (cy + offset_y) * scale_y;
00744   c.z = (cz + offset_z) * scale_z;
00745 
00746   return true;
00747 }
