00782 float DIOI2C6AXISTRACKINGLSM303DLHC::Heading(DIOI2CLSM303DLHCVECTOR& from)
00783 {
00784   DIOI2CLSM303DLHCVECTOR a;
00785   DIOI2CLSM303DLHCVECTOR c;
00786 
00787   if(!Read(a, c)) return 0.0f;
00788 
00789   DIOI2CLSM303DLHCVECTOR temp_a;
00790 
00791   temp_a.x = a.x;
00792   temp_a.y = a.y;
00793   temp_a.z = a.z;
00794 
00795   // normalize
00796   DIOI2CLSM303DLHCVECTOR::Normalize(temp_a);
00797 
00798   // compute E and N
00799   DIOI2CLSM303DLHCVECTOR e;
00800   DIOI2CLSM303DLHCVECTOR n;
00801 
00802   DIOI2CLSM303DLHCVECTOR::Cross(c ,temp_a, e);
00803 
00804   DIOI2CLSM303DLHCVECTOR::Normalize(e);
00805 
00806   DIOI2CLSM303DLHCVECTOR::Cross(temp_a, e , n);
00807 
00808   // compute heading
00809   float heading = (float)atan2( DIOI2CLSM303DLHCVECTOR::Dot(e , from), DIOI2CLSM303DLHCVECTOR::Dot(n , from)) * (180/PI);
00810   if (heading < 0) heading += 360;
00811 
00812   return float(heading);
00813 }
