00124 bool DIOI2C9AXISTRACKINGMPU9150::IniDevice()
00125 {
00126   if(!DIODEVICEI2C::IniDevice()) return false;
00127 
00128   if(diostream->Open())
00129     {
00130       if(diostream->WaitToConnected(timeout))
00131         {
00132           return DIODEVICE::Ini();
00133         }
00134     }
00135 
00136   return false;
00137 }
