00147 void DIOI2C9AXISTRACKINGMPU9150::Initialize()
00148 {
00149   SetClockSource(DIOI2CMPU9150_CLOCK_PLL_XGYRO);
00150   SetFullScaleGyroRange(DIOI2CMPU9150_GYRO_FS_250);
00151   SetFullScaleAccelRange(DIOI2CMPU9150_ACCEL_FS_2);
00152   SetSleepEnabled(false); // thanks to Jack Elston for pointing this one out!
00153 }
