01456 */
01467 /*-----------------------------------------------------------------*/
01468 void DIOI2C9AXISTRACKINGMPU9150::SetSlaveAddress(XBYTE num, XBYTE address)
01469 {
01470    if(num > 3) return;
01471 
01472    WriteByte(deviceaddr, DIOI2CMPU9150_RA_I2C_SLV0_ADDR + num*3, address);
01473 }
