03123 
03124 /*-------------------------------------------------------------------
03125 //  DIOI2C9AXISTRACKINGMPU9150::GetMotion9
03126 */
03144 /*-----------------------------------------------------------------*/
03145 void DIOI2C9AXISTRACKINGMPU9150::GetMotion9(XWORDSIG* ax, XWORDSIG* ay, XWORDSIG* az, XWORDSIG* gx, XWORDSIG* gy, XWORDSIG* gz, XWORDSIG* mx, XWORDSIG* my, XWORDSIG* mz)
03146 {
03147   //get accel and gyro
03148   GetMotion6(ax, ay, az, gx, gy, gz);
03149 
03150   //read mag
03151   WriteByte(deviceaddr, DIOI2CMPU9150_RA_INT_PIN_CFG, 0x02); //set i2c bypass enable pin to true to access magnetometer
03152   GEN_XSLEEP.MilliSeconds(10);
03153 
03154   WriteByte(DIOI2CMPU9150_RA_MAG_ADDRESS, 0x0A, 0x01); //enable the magnetometer
03155   GEN_XSLEEP.MilliSeconds(10);
03156 
03157   ReadBytes(DIOI2CMPU9150_RA_MAG_ADDRESS, DIOI2CMPU9150_RA_MAG_XOUT_L, 6, buffer);
03158   *mx = (((XWORDSIG)buffer[1]) << 8) | buffer[0];
03159   *my = (((XWORDSIG)buffer[3]) << 8) | buffer[2];
03160   *mz = (((XWORDSIG)buffer[5]) << 8) | buffer[4];
03161 }
03162 
03163 
03164 
03165 /*-------------------------------------------------------------------
03166 //  DIOI2C9AXISTRACKINGMPU9150::GetMag
03167 */
03179 /*-----------------------------------------------------------------*/
03180 void DIOI2C9AXISTRACKINGMPU9150::GetMag(XWORDSIG* mx, XWORDSIG* my, XWORDSIG* mz)
03181 {
03182   //read mag
03183   WriteByte(deviceaddr, DIOI2CMPU9150_RA_INT_PIN_CFG, 0x02); //set i2c bypass enable pin to true to access magnetometer
03184   GEN_XSLEEP.MilliSeconds(10);
03185 
03186   WriteByte(DIOI2CMPU9150_RA_MAG_ADDRESS, 0x0A, 0x01); //enable the magnetometer
03187   GEN_XSLEEP.MilliSeconds(10);
03188 
03189   ReadBytes(DIOI2CMPU9150_RA_MAG_ADDRESS, DIOI2CMPU9150_RA_MAG_XOUT_L, 6, buffer);
03190   *mx = (((XWORDSIG)buffer[1]) << 8) | buffer[0];
03191   *my = (((XWORDSIG)buffer[3]) << 8) | buffer[2];
03192   *mz = (((XWORDSIG)buffer[5]) << 8) | buffer[4];
03193 }
03194 
03195 
03196 
03197 
03198 /*-------------------------------------------------------------------
03199 //  DIOI2C9AXISTRACKINGMPU9150::GetMotion6
03200 */
03215 /*-----------------------------------------------------------------*/
03216 void DIOI2C9AXISTRACKINGMPU9150::GetMotion6(XWORDSIG* ax, XWORDSIG* ay, XWORDSIG* az, XWORDSIG* gx, XWORDSIG* gy, XWORDSIG* gz)
03217 {
03218   ReadBytes(deviceaddr, DIOI2CMPU9150_RA_ACCEL_XOUT_H, 14, buffer);
03219   *ax = (((XWORDSIG)buffer[0])  << 8) | buffer[1];
03220   *ay = (((XWORDSIG)buffer[2])  << 8) | buffer[3];
03221   *az = (((XWORDSIG)buffer[4])  << 8) | buffer[5];
03222   *gx = (((XWORDSIG)buffer[8])  << 8) | buffer[9];
03223   *gy = (((XWORDSIG)buffer[10]) << 8) | buffer[11];
03224   *gz = (((XWORDSIG)buffer[12]) << 8) | buffer[13];
03225 }
03226 
