03854 /*-------------------------------------------------------------------
03855 //  DIOI2C9AXISTRACKINGMPU9150::GetSlaveDelayEnabled
03856 */
03879 /*-----------------------------------------------------------------*/
03880 bool DIOI2C9AXISTRACKINGMPU9150::GetSlaveDelayEnabled(XBYTE num)
03881 {
03882   // DIOI2CMPU9150_DELAYCTRL_I2C_SLV4_DLY_EN_BIT is 4, SLV3 is 3, etc.
03883   if(num > 4) return false;
03884   ReadBit(deviceaddr, DIOI2CMPU9150_RA_I2C_MST_DELAY_CTRL, num, buffer);
03885   return buffer[0]?true:false;
03886 }
