05641 void DIOI2C9AXISTRACKINGMPU9150::SetMemoryBank(XBYTE bank, bool prefetchEnabled, bool userBank)
05642 {
05643   bank &= 0x1F;
05644 
05645   if(userBank)        bank |= 0x20;
05646   if(prefetchEnabled) bank |= 0x40;
05647 
05648   WriteByte(deviceaddr, DIOI2CMPU9150_RA_BANK_SEL, bank);
05649 }
