00150 void DIOI2CBATTERYCHARGERBQ24295::Update()
00151 {  
00152   if(!charging) return;
00153             
00154   // For an unknown reason the stat pin generates an square signal and not holds the low level when is charging
00155   // so I have to read the register to know when the charge is done
00156   //I read the register every 5 seconds
00157     
00158   XBYTE read_reg_fault;        
00159   XBYTE read_reg_status;
00160       
00161   if(ReadRegister(DIOI2CBATTERYCHARGERBQ24295_FAULT_REG_REG09   , read_reg_fault)  != 0)   return;            //TODO-GFR detect FAULT errors        
00162   if(ReadRegister(DIOI2CBATTERYCHARGERBQ24295_SYS_STATUS_REG08  , read_reg_status) != 0)   return;            
00163         
00164   if(((read_reg_status >> 4) & 0x03) == 0x03) fullycharge = true;          
00165 }
