00320 bool DIOI2CBATTERYCHARGERBQ24295::Initialize()
00321 {
00322   // Charger GPIO Inputs
00323   GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_STAT]  , DIOGPIO_MODE_INPUT);          
00324             
00325   // Charger GPIO Outputs
00326   GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_QON]   , DIOGPIO_MODE_OUTPUT);
00327   GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_CE]    , DIOGPIO_MODE_OUTPUT);
00328           
00329   // Charger GPIO Interrupts
00330   DIOGPIO_ENTRY* interrupt = GEN_DIOGPIO.GPIOEntry_GetByID(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_INT]);
00331   if(interrupt)
00332     {
00333       interrupt->SetIntFunctionPointer(DIOI2CBATTERYCHARGERBQ24295::Handler_Interrupt);      
00334       interrupt->SetIntParamPointer((void*)this);      
00335       GEN_DIOGPIO.SetMode(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_INT], DIOGPIO_MODE_INTERRUPT); 
00336     }
00337           
00338   // Charger disabled
00339   GEN_DIOGPIO.SetValue(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_CE]  , true);                     
00340           
00341   // Shipping Mode off
00342   GEN_DIOGPIO.SetValue(GPIOentryID[DIOI2CBATTERYCHARGERBQ24295_GPIOENTRYID_QON]  , false);           
00343           
00344   charging    = false;
00345   fault       = 0;
00346   fullycharge = false;
00347           
00348   // Configure the charger ILMAX to 500mA  
00349   XBYTE read_reg;
00350 
00351   if(!ReadRegister(DIOI2CBATTERYCHARGERBQ24295_INPUT_SRC_CTRL_REG00, read_reg))         return false;
00352           
00353   // IINLIM bits (010) (500mA)
00354   read_reg &= 0xF8;
00355   read_reg |= 0x02;
00356           
00357   if(!WriteRegister(DIOI2CBATTERYCHARGERBQ24295_INPUT_SRC_CTRL_REG00, read_reg))        return false;
00358                    
00359   // Disable the charger watchdog timer and the safety timer
00360   if(!ReadRegister(DIOI2CBATTERYCHARGERBQ24295_CHRG_TERM_TIMER_CTRL_REG05, read_reg))   return false;
00361           
00362   // Disable timer
00363   read_reg &= 0xC7;
00364   if(!WriteRegister(DIOI2CBATTERYCHARGERBQ24295_INPUT_SRC_CTRL_REG00, read_reg))        return false;          
00365                                 
00366   return true;  
00367 }
