00402 bool DIOI2CBATTERYCHARGERBQ24295::ReadRegister(XBYTE offset, XBYTE* data, XDWORD sizedata)
00403 {
00404   if(!diostream) return false;
00405 
00406   bool status;
00407 
00408   status = diostream->Write(&offset, 1)?true:false;
00409   if(status) status = diostream->WaitToFlushOutXBuffer(timeout);
00410 
00411   if(!status) return false;
00412 
00413   status = diostream->WaitToFilledReadingBuffer(sizedata, timeout);
00414   if(status) status = (diostream->Read(data, sizedata) == sizedata)?true:false;
00415 
00416   return status;
00417 }
