00451 bool DIOI2CBATTERYCHARGERBQ24295::WriteRegister(XBYTE offset, XBYTE* data, XDWORD sizedata)
00452 {
00453   if(!diostream) return false;
00454 
00455   XBUFFER buffer;
00456   bool    status;
00457 
00458   buffer.Add((XBYTE)offset);
00459   if(data && sizedata) buffer.Add(data, sizedata);
00460 
00461   status = diostream->Write(buffer)?true:false;
00462   if(status) status = diostream->WaitToFlushOutXBuffer(timeout);
00463 
00464   return status;
