00929 bool DIOIEC60870_5::CMD_C_AC_NA_2_IniSession(int timeout)
00930 {
00931   XBYTE retfunctioncode;
00932   bool  retisfixmsg;
00933 
00934   if(!CMD_RequestStatusLink(timeout)) return false;
00935   if(!CMD_ResetRemoteLink(timeout))   return false;
00936   if(!CMD_RequestStatusLink(timeout)) return false;
00937 
