00943 
00944   asdu.Add((XBYTE)DIOIEC60870_5_ASDUID_C_AC_NA_2_SESSIONINI); // Identificator
00945   asdu.Add((XBYTE)1);                                         // Object Number
00946   asdu.Add((XBYTE)DIOIEC60870_5_ASDUCAUSETRANS_ACTIVATION);   // Transmision Cause
00947   asdu.Add((XBYTE)addressPM);                                 // Meter Point Address
00948   asdu.Add((XBYTE)00);                                        // Register Address  = 0
00949   asdu.Add((XBYTE)00);                                        // Register Address  = 0
00950 
00951   XDWORD _keyPM = keyPM;
00952 
00953   if(inlittleendian) SWAPDWORD(_keyPM);
00954   asdu.Add((XDWORD)_keyPM);                                   // Meter Point Key
00955 
00956   if(!SendMsgVar(DIOIEC60870_5_MASTERFUNCUSERDATA,&asdu))   return false;
00957   if(!ReadMsg(retfunctioncode,retisfixmsg,NULL,timeout))    return false;
00958 
00959   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCCONFIRMACK)
00960     {
00961       XBUFFER retasdu;
00962 
00963       if(CMD_RequestUserDataClass2(&retasdu,timeout))
00964         {
00965           XBYTE* buffer = retasdu.Get();
00966           if(!buffer) return false;
00967 
00968           if(buffer[0]!=DIOIEC60870_5_ASDUID_C_AC_NA_2_SESSIONINI)      return false;
00969           if(buffer[2]!=DIOIEC60870_5_ASDUCAUSETRANS_CONFIRMACTIVATION) return false;
00970 
00971           DIOIEC60870_5_XEVENT xevent(this,DIOIEC60870_5_XEVENT_TYPE_C_AC_NA_2_INISESSION);
00972           PostEvent(&xevent);
00973 
00974           return true;
00975         }
00976 
00977       return false;
00978     }
00979 
00980   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCNOCONFIRMACK)  return false;
00981 
00982   return false;
