01054 bool DIOIEC60870_5::CMD_C_CI_NU_2_Init(int TM,XDATETIME& timestart,XDATETIME& timeend,int timeout)
01055 {
01056   XBYTE retfunctioncode;
01057   bool retisfixmsg;
01058 
01059   XBUFFER asdu;
01060 
01061   asdu.Add((XBYTE)DIOIEC60870_5_ASDUID_C_CI_NU_2);              // Identificator
01062   asdu.Add((XBYTE)01);                                          // Object Number
01063   asdu.Add((XBYTE)DIOIEC60870_5_ASDUCAUSETRANS_ACTIVATION);     // Transmision Cause
01064   asdu.Add((XBYTE)addressPM);                                   // Meter Point Address
01065   asdu.Add((XBYTE)0x00);                                        // Register Address
01066   asdu.Add((XBYTE)((TM==1)?0x0B:0x0C));                         // Register Address
01067   asdu.Add((XBYTE)0x01);                                        // Address of Total First
01068   asdu.Add((XBYTE)0x08);                                        // Address of Total Last
01069 
01070   XBYTE resulttime[DIOIEC60870_5_SIZEMAXTIMELABELTYPEA];
01071 
01072   SetTimeLabelTypeA(timestart,false,false,false,resulttime);    asdu.Add(resulttime,DIOIEC60870_5_SIZEMAXTIMELABELTYPEA);
01073   SetTimeLabelTypeA(timeend  ,false,false,false,resulttime);    asdu.Add(resulttime,DIOIEC60870_5_SIZEMAXTIMELABELTYPEA);
01074 
01075   if(!SendMsgVar(DIOIEC60870_5_MASTERFUNCUSERDATA,&asdu))   return false;
01076   if(!ReadMsg(retfunctioncode,retisfixmsg,NULL,timeout))    return false;
01077 
01078   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCCONFIRMACK)
01079     {
01080       XBUFFER retasdu;
01081       if(!CMD_RequestUserDataClass2(&retasdu,timeout)) return false;
01082 
01083       return true;
01084     }
01085 
01086   if(retfunctioncode==DIOIEC60870_5_SLAVEFUNCNOCONFIRMACK)  return false;
01087 
01088   return false;
01089 }
