01104 bool DIOIEC60870_5::CMD_C_CI_NU_2_Read(DIO_C_CI_NU_2_RESULT* result,int timeout)
01105 {
01106   if(!result) return false;
01107 
01108   XBUFFER retasdu;
01109 
01110   if(!CMD_RequestUserDataClass2(&retasdu,timeout)) return false;
01111 
01112   DIOIEC60870_5_XEVENT xevent(this,DIOIEC60870_5_XEVENT_TYPE_C_CI_NU_2_READVALUE);
01113   PostEvent(&xevent);
01114 
01115   XBYTE* buffer = retasdu.Get();
01116   if(!buffer) return false;
01117 
01118   bool rate   = false;
01119   bool VI     = false;
01120   bool summer = false;
01121 
01122   GetTimeLabelTypeA(&buffer[54],rate,VI,summer,(*result->GetDateTime()));
01123 
01124   for(int c=6;c<(int)retasdu.GetSize()-8;c+=6)
01125     {
01126       XDWORD value = 0;
01127 
01128       for(int d=0;d<4;d++)
01129         {
01130           value |= (XDWORD)buffer[c+1+(3-d)];
01131           if(d<3) value<<=8;
01132         }
01133 
01134       result->GetIndexValues()->Add(buffer[c]);
01135       result->GetValues()->Add(value);
01136     }
01137 
01138   return true;
01139 }
