01374 bool DIOIEC60870_5::SetTimeLabelTypeA(XDATETIME& time, bool rate, bool VI, bool summer,XBYTE* result)
01375 {
01376   memset(result,0,DIOIEC60870_5_SIZEMAXTIMELABELTYPEA);
01377 
01378   result[0] = time.GetMinutes() & 0x00FF;       // add valid flag and genuine flag
01379   result[1] = time.GetHours()   & 0x00FF;       // add summer flag
01380   result[2] = ((time.GetDayOfWeek()%7)&0x03)<<5 | (time.GetDay() & 0x1F);
01381   result[3] = time.GetMonth()   & 0x0F;
01382   result[4] = time.GetYear() - 2000;
01383 
01384   return true;
01385 }
