01403 bool DIOIEC60870_5::SetTimeLabelTypeB(XDATETIME& time, bool rate, bool VI, bool summer,XBYTE* result)
01404 {
01405   memset(result,0,DIOIEC60870_5_SIZEMAXTIMELABELTYPEB);
01406 
01407   XDWORD m = time.GetMilliSeconds() + 1000 * time.GetSeconds();
01408 
01409   result[0] = (m & 0xFF);
01410   result[1] = (m & 0xFF00)>>8;
01411   result[2] = time.GetMinutes() & 0x00FF;       // add valid flag and genuine flag
01412   result[3] = time.GetHours()   & 0x00FF;       // add summer flag
01413   result[4] = ((time.GetDayOfWeek()%7)&0x03)<<5 | (time.GetDay() & 0x1F);
01414   result[5] = time.GetMonth()   & 0x0F;
01415   result[6] = time.GetYear() - 2000;
01416 
01417   return true;
