01553     {
01554       case DIOIEC60870_5_MASTERFUNCRESETREMOTELINK        : isFCVon = false;  break;
01555       case DIOIEC60870_5_MASTERFUNCRESETUSERPROCESS      : isFCVon = false;  break;
01556       case DIOIEC60870_5_MASTERFUNCUSERDATA               : isFCVon = true;   break;
01557       case DIOIEC60870_5_MASTERFUNCREQUESTSTATUSLINK      : isFCVon = false;  break;
