01565 
01578 bool DIOIEC60870_5::SendMsgVar(XBYTE functioncode, XBUFFER* dataASDU)
01579 {
01580   if(!diostream) return false;
01581   if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return false;
01582   if(!xtimer)    return false;
01583 
01584   if(!dataASDU)  return false;
01585 
01586   XBUFFER xbuffer;
01587   XBYTE   controlfield = 0;
01588   
01589   xbuffer.Add((XBYTE)DIOIEC60870_5_MARKSTARTVAR);
01590   xbuffer.Add((XBYTE)0);
01591   xbuffer.Add((XBYTE)0);
01592   xbuffer.Add((XBYTE)DIOIEC60870_5_MARKSTARTVAR);
01593 
01594   controlfield  = 0x00;
01595   controlfield |= DIOIEC60870_5_CONTROLDIRPRIMARY;
01596 
01597   if(IsValidFrameCount(functioncode))
01598     {
01599       controlfield |= FCBstate?DIOIEC60870_5_CONTROLFCB:0x00;
01600       controlfield |= DIOIEC60870_5_CONTROLFCV;
01601 
01602       FCBstate = !FCBstate;
01603     }
01604 
01605   controlfield |= (functioncode&0x0F);
01606   xbuffer.Add(controlfield);
01607 
01608   if(!havelongaddress)
01609     {
01610       xbuffer.Add((XBYTE)addressfield);
01611     }
01612    else
01613     {
01614       XWORD _addressfield = addressfield;
01615 
01616       if(inlittleendian) SWAPWORD(_addressfield);
01617       xbuffer.Add((XWORD)_addressfield);
01618     }
01619 
01620   xbuffer.Add(dataASDU);
01621 
01622   xbuffer.Set((XBYTE)(dataASDU->GetSize()+3), 1);
01623   xbuffer.Set((XBYTE)(dataASDU->GetSize()+3), 2);
01624 
01625   xbuffer.Add((XBYTE)0);
01626   xbuffer.Add((XBYTE)DIOIEC60870_5_MARKEND);
01627 
01628   xbuffer.Set(CalculateCheckSum(xbuffer), (xbuffer.GetSize()-2));
01629 
01630   if(!diostream->Write(xbuffer.Get(),xbuffer.GetSize())) return false;
01631 
01632   return true;
01633 }
