01708 bool DIOIEC60870_5::WaitToReadMsg(XDWORD size,int timeout)
01709 {
01710   XDWORD actualsize = 0;
01711 
01712   if(!xtimer) return false;
01713   xtimer->Reset();
01714 
01715   do{
01716       if(canceloperations)                                         return false;
01717 
01718       if(!diostream)                                               return false;
01719       if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return false;
01720 
01721       actualsize = diostream->GetInXBuffer()->GetSize();
01722       if((int)xtimer->GetMeasureSeconds() >= timeout) return false;
01723 
01724       GEN_XSLEEP.MilliSeconds(10);
01725 
01726       if(actualsize >= size) return true;
01727 
01728     } while(!actualsize);
01729 
01730   if(!actualsize) return false;
01731 
01732   return true;
01733 }
