01735 
01750 bool DIOIEC60870_5::ReadMsg(XBYTE& retfunctioncode,bool& retisfixmsg,XBUFFER* retASDU,int timeout)
01751 {
01752   if(!diostream) return false;
01753   if(diostream->GetConnectStatus()!=DIOSTREAMSTATUS_CONNECTED) return false;
01754   if(!xtimer)    return false;
01755 
01756   bool status  = false;
01757 
01758   XBUFFER xbuffer((XDWORD)DIOIEC60870_5_MAXSIZE, true);
01759 
01760   if(!WaitToReadMsg(DIOIEC60870_5_MAXSIZE,timeout))
01761     {
01762       return false;
01763     }
01764 
01765   int indexbufferread = 0;
01766 
01767   xtimer->Reset();
01768 
01769   while(1)
01770     {
01771       if(canceloperations) return false;
01772 
01773       if((int)xtimer->GetMeasureSeconds()>=timeout) return false;
01774 
01775       if((indexbufferread+DIOIEC60870_5_SIZEMSGFIX)>(int)diostream->GetInXBuffer()->GetSize()) break;
01776 
01777       XBYTE* buffer = &(diostream->GetInXBuffer()->Get()[indexbufferread]);
01778 
01779       switch(buffer[0])
01780         {
01781           case DIOIEC60870_5_MARKSTARTFIX : { XBYTE checksum = CalculateCheckSum(buffer,DIOIEC60870_5_SIZEMSGFIX);
01782                                               if(checksum!=buffer[4])  break;
01783 
01784                                               XWORD _addressfield;
01785 
01786                                               _addressfield   = buffer[2];
01787                                               _addressfield <<= 8;
01788                                               _addressfield  |= buffer[3];
01789 
01790                                               if(inlittleendian) SWAPWORD(_addressfield);
01791 
01792                                               if(_addressfield != addressfield)
01793                                                 {
01794                                                   indexbufferread += DIOIEC60870_5_SIZEMSGFIX;
01795                                                   continue;
01796                                                 }
01797 
01798                                               XBYTE controlfield = buffer[1];
01799                                               if(controlfield&DIOIEC60870_5_CONTROLDIRPRIMARY)  break;
01800 
01801                                               retfunctioncode   = (controlfield&0x0f);
01802                                               retisfixmsg       = true;
01803                                               diostream->GetInXBuffer()->Extract(NULL,indexbufferread,DIOIEC60870_5_SIZEMSGFIX);
01804                                             }
01805 
01806                                             status = true;
01807                                             break;
01808 
01809           case DIOIEC60870_5_MARKSTARTVAR : { XBYTE sizeasdu  = buffer[1];
01810                                               XBYTE checksum = CalculateCheckSum(buffer,sizeasdu+4+2);
01811                                               if(checksum!=buffer[sizeasdu+4])  break;
01812 
01813                                               XWORD _addressfield;
01814 
01815                                               _addressfield   = buffer[5];
01816                                               _addressfield <<= 8;
01817                                               _addressfield  |= buffer[6];
01818 
01819                                               if(inlittleendian) SWAPWORD(_addressfield);
01820 
01821                                               if(_addressfield != addressfield)
01822                                                 {
01823                                                   indexbufferread += DIOIEC60870_5_SIZEMSGFIX;
01824                                                   continue;
01825                                                 }
01826 
01827                                               XBYTE controlfield = buffer[4];
01828                                               if(controlfield&DIOIEC60870_5_CONTROLDIRPRIMARY) break;
01829 
01830                                               retfunctioncode   = (controlfield&0x0f);
01831                                               retisfixmsg       = false;
01832 
01833                                               if(retASDU) retASDU->Add(&buffer[7],sizeasdu-3);
01834 
01835                                               diostream->GetInXBuffer()->Extract(NULL,indexbufferread,sizeasdu+4+2);
01836                                             }
01837 
01838                                             status = true;
01839                                             break;
01840         }
01841     }
01842 
01843   return status;
01844 }
