01846 
01859 XBYTE DIOIEC60870_5::CalculateCheckSum(XBYTE* buffer,XDWORD sizebuffer)
01860 {
01861   if(!buffer)       return 0;
01862 
01863   XBYTE csum    = 0;
01864   int  start  = 0;
01865   int  size   = 0;
01866 
01867   switch(buffer[0])
01868     {
01869       case DIOIEC60870_5_MARKSTARTFIX : if(buffer[4]==DIOIEC60870_5_MARKEND)
01870                                           {
01871                                             start = 1;
01872                                             size  = 2;
01873                                           }
01874 
01875                                         if(buffer[5]==DIOIEC60870_5_MARKEND)
01876                                           {
01877                                             start = 1;
01878                                             size  = 3;
01879                                           }
01880                                         break;
01881 
01882       case DIOIEC60870_5_MARKSTARTVAR : if(buffer[3]==DIOIEC60870_5_MARKSTARTVAR)
01883                                           {
01884                                             start = 4;
01885                                             size = sizebuffer-start-2;
01886                                           }
01887                                         break;
01888     }
01889 
01890 
01891   for(int c=0;c<size;c++)
01892     {
01893       csum += buffer[start+c];
01894     }
01895 
01896   return csum;
01897 }
01898 
01899 
01911 XBYTE DIOIEC60870_5::CalculateCheckSum(XBUFFER& xbuffer)
01912 {
01913   return CalculateCheckSum(xbuffer.Get(),xbuffer.GetSize());
01914 }
