01929 int DIOIEC60870_5::GetIntFromBuffer(XBYTE* buffer,XDWORD size)
01930 {
01931   XDWORD value = 0;
01932 
01933   for(XDWORD d=0; d<size; d++)
01934     {
01935       value |= (XDWORD)buffer[((size-1)-d)];
01936       if(d<(size-1)) value<<=8;
01937     }
01938 
01939   return (int)value;
01940 }
