00594                                       break;    
00595 
00596       case DBUS_TYPE_STRING         : 
00597       case DBUS_TYPE_OBJECT_PATH    : { char* value_str_ptr = NULL;
00598                                         dbus_message_iter_get_basic(&iter_variant, &value_str_ptr);
00599                                         if(value_str_ptr)
00600                                           {
00601                                             result = value_str_ptr;
00602                                             status = true;
00603                                           }
00604                                       }
00605                                       break;
00606 
00607 
00608     }
00609 
00610   return status;
