00757 bool DIOLINUXDBUS::Ini(DBusBusType type)
00758 {
00759   DBusError error;
00760 
00761   // --------------------------------------------------
00762   // initialise the errors
00763   dbus_error_init(&error);
00764 
00765   connection = dbus_bus_get(type, &error);
00766   if(!connection)
00767     {
00768       DIOLINUXDBUS::TraceError(error);
00769       return false;
00770     }
00771  
00772   connection_name = dbus_bus_get_unique_name(connection);
00773   XTRACE_PRINTCOLOR(XTRACE_COLOR_GREEN, __L("[DBUS] Ini connection [%08X] %s ... "), connection, connection_name.Get());
00774     
00775   thread_signals = CREATEXTHREAD(XTHREADGROUPID_DIOLINUXDBUS, __L("DIOLINUXDBUS::Ini"), Thread_Signals, (void*)this);
00776   if(!thread_signals) return false;
00777 
00778   return thread_signals->Ini();
00779 }
