00853 bool DIOLINUXDBUS::Signal_Add(XCHAR* type, XCHAR* name)
00854 {
00855   if(!connection) return false;
00856 
00857   XSTRING     typestr;
00858   DBusError   error;
00859   bool        status = true;
00860 
00861   if(!Signal_IsTypeAdd(type))
00862     {
00863       typestr.Format(__L("type='signal', interface='%s'"), type); 
00864 
00865       XBUFFER charstr;
00866       
00867       typestr.ConvertToASCII(charstr);      
00868       dbus_bus_add_match(connection, charstr.GetPtrChar(), &error);    // see signals from the given interface
00869       
00870       dbus_connection_flush(connection);
00871 
00872       if(dbus_error_is_set(&error)) 
00873         { 
00874           DIOLINUXDBUS::TraceError(error);
00875           status = false;   
00876         }
00877 
00878       //XTRACE_PRINTCOLOR((status?XTRACE_COLOR_BLUE:XTRACE_COLOR_RED), __L("[DBUS] Signal Add Type [%s] ..."), typestr.Get()); 
00879     }
00880 
00881   if(!status) return status;
00882   
00883   DIOLINUXDBUS_SIGNAL* signal = new DIOLINUXDBUS_SIGNAL();
00884   if(signal)
00885     {
00886       signal->GetType()->Set(type);
00887       signal->GetName()->Set(name);
00888 
00889       signals.Add(signal);    
00890 
00891     } else status = false;
00892     
00893   //XTRACE_PRINTCOLOR((status?XTRACE_COLOR_BLUE:XTRACE_COLOR_RED), __L("[DBUS] Signal Add Name [%s] ..."), name); 
00894 
00895   return status;
00896 }
