00928 bool DIOLINUXDBUS::Signal_IsTypeAdd(XCHAR* type)
00929 {
00930   if(signals.IsEmpty()) return false;
00931 
00932   for(XDWORD c=0; c<signals.GetSize(); c++)
00933     {
00934       DIOLINUXDBUS_SIGNAL* signal = signals.Get(c);
00935       if(signal)
00936         {
00937           if(!signal->GetType()->Compare(type, true)) return true;
00938         }
00939     }
00940 
00941   return false;
00942 }
