01032 void DIOLINUXDBUS::Thread_Signals(void* param)
01033 {
01034   DIOLINUXDBUS* dbus = (DIOLINUXDBUS*)param;
01035   if(!dbus) return;
01036 
01037   if(!dbus->connection)       return;
01038   if(dbus->signals.IsEmpty()) return;
01039 
01040   DBusMessage* handler_message = NULL;
01041 
01042   dbus_connection_read_write(dbus->connection, 0);
01043   handler_message = dbus_connection_pop_message(dbus->connection);
01044 
01045   if(!handler_message) 
01046     { 
01047       GEN_XSLEEP.Seconds(1);
01048       return;
01049     }
01050 
01051   //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[DBUS] Message ...")); 
01052 
01053   DIOLINUXDBUS_SIGNAL*  signal        = NULL;
01054   bool                  found_signal  = false;
01055 
01056   for(XDWORD c=0; c<dbus->signals.GetSize(); c++)
01057     {
01058       signal = dbus->signals.Get(c);
01059       if(signal)
01060         {
01061           // ---------------------------------------------------------------------------------------
01062           // check if the message is a signal from the correct interface and with the correct name
01063 
01064           XBUFFER typechar;
01065           XBUFFER namechar;
01066  
01067           (*signal->GetType()).ConvertToASCII(typechar);
01068           (*signal->GetName()).ConvertToASCII(namechar);
01069 
01070           if(dbus_message_is_signal(handler_message, typechar.GetPtrChar(), namechar.GetPtrChar()))  
01071             {
01072               found_signal = true;  
01073             }
01074           
01075           if(found_signal) 
01076             {
01077               break;
01078             }
01079            else 
01080             {
01081               signal = NULL;
01082             }
01083         }
01084     }
01085 
01086 
01087   if(found_signal && signal) 
01088     {
01089       DIOLINUXDBUS_MESSAGE message;
01090 
01091       message.SetHandler(handler_message);  
01092 
01093       DIOLINUXDBUS_XEVENT xevent(dbus, DIOLINUXDBUS_XEVENT_TYPE_SIGNAL);    
01094   
01095       xevent.SetMessage(&message);
01096       xevent.SetSignal(signal);
01097 
01098       dbus->PostEvent(&xevent);
01099 
01100       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[DBUS] Signal produced [%s] [%s] ..."), signal->GetType()->Get(), signal->GetName()->Get());    
01101     }   
01102    else
01103     {
01104       dbus_message_unref(handler_message);
01105     }
01106 }
