00233 int DIOLINUXSTREAMBLUETOOTH::IsReadyConnect(int socket)
00234 {
00235   struct timeval  tv;
00236   int             rc;
00237   fd_set          fdr;
00238   fd_set          fdw;
00239   fd_set          fds;
00240 
00241   if(socket==-1) return -1;
00242 
00243   FD_ZERO(&fdr);
00244   FD_ZERO(&fdw);
00245   FD_ZERO(&fds);
00246 
00247   FD_SET((unsigned int)socket,&fdr);
00248   FD_SET((unsigned int)socket,&fdw);
00249   FD_SET((unsigned int)socket,&fds);
00250 
00251   tv.tv_sec  = 0;
00252   tv.tv_usec = 100;
00253 
00254   rc = select(socket+1, &fdr, &fdw, &fds, &tv);
00255   if(rc==-1) return -1;
00256   if(rc== 2) return -1;
00257 
00258   int status1 = FD_ISSET(socket,&fdr) ? 1 : 0;
00259   int status2 = FD_ISSET(socket,&fdw) ? 1 : 0;
00260   int status3 = FD_ISSET(socket,&fds) ? 1 : 0;
00261 
00262   if(status3) return -1;
00263   if(((status1) || (status2)) && (rc==1))  return  1;
00264 
00265   return 0;
00266 }
